import React from 'react';
import axios from 'axios';
import {backendURL} from '../../imp';
import {Dropdown,Form,Button} from 'react-bootstrap'


class AddSubject extends React.Component{
    constructor(props){
        super(props);
        this.state={
            data:[],
            selected:{classID:null,name:"Select Class"},
            loading:true,
            error:false,
            add:true,
            showForm:false
        }
        this.selected=this.selected.bind(this);
    }

    selected(info){
        this.setState({selected:{classID:info._id,name:info.name},showForm:true});
    }

    componentDidMount(){
        axios.get(`${backendURL}/request/class`).then(res=>{
            this.setState({data:res.data,loading:false});
        }).catch(err=>{
            this.setState({error:true})
        })
    }

    render(){
        console.log(this.state);
        const renderElem=this.state.data.map(i=>{
            return(
            <Dropdown.Item onClick={()=>this.selected(i)}>{i.name}</Dropdown.Item>
            )
        });
        return(
            <div style={{marginTop:"100px",textAlign:"center"}}>
                <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                        {this.state.selected.name}
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                        {renderElem}
                    </Dropdown.Menu>
                </Dropdown>
                <div>
                <Form>
                    <Form.Group controlId="formBasicEmail">
                        <Form.Label>Add Subject</Form.Label>
                        <Form.Control type="email" placeholder="Enter subject" />
                    </Form.Group>
                    <Button variant="primary">
                        Add
                    </Button>
                </Form>
                </div>
            </div>
        )
    }
}

export default AddSubject;
