import React from 'react';
import axios from 'axios';
import {backendURL} from '../../imp';
import {Dropdown,Form,Button} from 'react-bootstrap'

class Admin extends React.Component{
    constructor(){
        super();
        this.state={
            error:false,
            data:[],
            selected:"Nothing",
            loading:true
        };
        this.Selected=this.Selected.bind(this);
        this.addSubject=this.addSubject.bind(this);
        this.subjectRef=React.createRef();
    }

    Selected(name){
        this.setState({selected:name});
    }

    addSubject(){
        console.log(this.subjectRef.current.value);
        this.setState({loading:true});
        axios.post(`${backendURL}/request/addSubject`,{
            name:this.subjectRef.current.value,
            questions:[],
            classID:this.state.selectedClass.classID
        }).then(res=>{
            this.setState({loading:false})
        }).catch(err=>{
            this.setState({error:true});
        })
    }

    componentDidMount(){
        if(this.state.selected!=="Nothing"){
            // axios.get(`${backendURL}/request/${}`).then(res=>{
            //     this.setState({class:res.data,loading:false});
            // }).catch(err=>{this.setState({error:true})});
        }
        else
            this.setState({loading:false})
    }

    render(){
        console.log(this.state);
        if(this.state.loading){
            return(
                <div style={{marginTop:"100px",textAlign:"center"}}>Please Wait Loading</div>
            )
        }
        else if(this.state.selected==="subject"){
            return(
                <div style={{marginTop:"100px",textAlign:"center"}}>
                    <h1>Subject</h1>
                </div>
            )
        }
        else if(this.state.selected==="topic"){
            return(
                <div style={{marginTop:"100px",textAlign:"center"}}>
                    <h1>Topic</h1>
                </div>
            )
        }
        else if(this.state.selected==="question"){
            return(
                <div style={{marginTop:"100px",textAlign:"center"}}>
                    <h1>Question</h1>
                </div>
            )
        }
        else{
            return(
                <div style={{marginTop:"100px",textAlign:"center"}}>
                    <Button onClick={()=>this.Selected("subject")}>SUBJECT</Button><br/><br/>
                    <Button onClick={()=>this.Selected("topic")}>TOPIC</Button><br/><br/>
                    <Button onClick={()=>this.Selected("question")}>QUESTION</Button><br/><br/>
                </div>
            )
        }
    }
}

export default Admin;
