import React from 'react';
import {backendURL} from '../../imp';
import axios from 'axios';
import {Dropdown,Form,Button} from 'react-bootstrap'


class AddQuestion extends React.Component{
    constructor(props){
        super(props);
        this.state={
            data:[],
            loading:true,
            selected:{topicID:null,name:"Select Topic"},
            showForm:true
        }
        this.topicRef=React.createRef();
        this.selected=this.selected.bind(this);
    }

    selected(info){
        this.setState({selected:{topicID:info._id,name:info.name},isSelected:true});
    }
    componentDidMount(){
        axios.get(`${backendURL}/request/topics/${this.props.subjectID}`).then(res=>{
            this.setState({data:res.data.topics,loading:false})
        }).catch(err=>{this.setState({error:true})})
    }
    render(){
        const renderElem=this.state.data.map(i=>{
            return(
                <Dropdown.Item onClick={()=>this.selected(i)}>{i.name}</Dropdown.Item>
            )
        })
        return(
            <div style={{marginTop:"100px",textAlign:"center"}}>
                        <Dropdown>
                            <Dropdown.Toggle variant="success" id="dropdown-basic">
                                {this.state.selected.name}
                            </Dropdown.Toggle>
                            <Dropdown.Menu>
                                {renderElem}
                            </Dropdown.Menu>
                        </Dropdown>
                        <div hidden={this.state.showForm}>
                        <Form>
                            <Form.Group controlId="formBasicEmail">
                                <Form.Label>Add Topic</Form.Label>
                                <Form.Control ref={this.topicRef} type="text" placeholder="Enter topic" />
                            </Form.Group>
                            <Button onClick={this.addTopic} variant="primary">
                                Add
                            </Button>
                        </Form>
                        </div>
                    </div>
        )
    }
}

export default AddQuestion;
