import React from 'react';
import axios from 'axios';
import {backendURL} from '../../imp';
import {Dropdown,Form,Button} from 'react-bootstrap'

class Admin extends React.Component{
    constructor(){
        super();
        this.state={
            error:false,
            data:[],
            selected:{what:"Nothing",info:{classID:null,name:"Select Class",show:false}},
            loading:true
        };
        this.classSelected=this.classSelected.bind(this);
        this.addSubject=this.addSubject.bind(this);
        this.subjectRef=React.createRef();
    }

    addSubject(){
        console.log(this.subjectRef.current.value);
        this.setState({loading:true});
        axios.post(`${backendURL}/request/addSubject`,{
            name:this.subjectRef.current.value,
            questions:[],
            classID:this.state.selectedClass.classID
        }).then(res=>{
            this.setState({loading:false})
        }).catch(err=>{
            this.setState({error:true});
        })
    }

    classSelected(id,name){
        this.setState({selectedClass:{classID:id,name:name,show:true}});
    }

    componentDidMount(){
        if(this.state.selected.what!=="Nothing"){
            axios.get(`${backendURL}/request/class`).then(res=>{
                this.setState({class:res.data,loading:false});
            }).catch(err=>{this.setState({error:true})});
        }
    }

    render(){
        const renderElement=this.state.class.map(i=>{
            return(
            <Dropdown.Item onClick={()=>{this.classSelected(i._id,i.name)}}>{i.name}</Dropdown.Item>
            )
        });
        if(this.state.selectedClass.show){
            return(
                <div style={{marginTop:"100px",textAlign:"center"}}>
                    <Button onClick={()=>this.select("subject")}>SUBJECT</Button>
                    <Button onClick={()=>this.select("topic")}>TOPICS</Button>
                    <Button onClick={()=>this.select("question")}>QUESTION</Button>
                </div>

                    // <div style={{marginTop:"100px",textAlign:'center'}}>
                    //     <Dropdown>
                    //     <Dropdown.Toggle variant="success" id="dropdown-basic">
                    //         {this.state.selectedClass.name}
                    //     </Dropdown.Toggle>
                    //     <Dropdown.Menu>
                    //         {renderElement}
                    //     </Dropdown.Menu>
                    //     </Dropdown>
                    //     <Form style={{width:"50%",marginLeft:"10%"}}>
                    //         <Form.Group controlId="formBasicEmail">
                    //             <Form.Label>Add Subject</Form.Label>
                    //             <Form.Control ref={this.subjectRef} type="text" placeholder="Enter subject name" />
                    //         </Form.Group>
                    //         <Button onClick={this.addSubject} variant="primary">
                    //             ADD
                    //         </Button>
                    //     </Form>
                    // </div>
            )
        }
        else if(this.state.loading){
            return(
                <div style={{marginTop:"100px",textAlign:"cnter"}}><h1>Please Wait Loading</h1></div>
            )
        }
        else{
            return(
                <div style={{marginTop:"100px",textAlign:'center'}}>
                    <Dropdown>
                    <Dropdown.Toggle variant="success" id="dropdown-basic">
                        {this.state.selectedClass.name}
                    </Dropdown.Toggle>
                    <Dropdown.Menu>
                    {renderElement}
                    </Dropdown.Menu>
                    </Dropdown>
                </div>
            )
        }
    }
}

export default Admin;
