import React from 'react';
import EachQuestion from './EachQuestion';

class Question extends React.Component{

    constructor(props){
        super(props);
        // console.log("yes2");
        this.state={
            class:"",
            subject:"",
            topic:"",
            questions:[{}],
            score:0
        }
        this.CorrectAnswer=this.CorrectAnswer.bind(this);
    }

    CorrectAnswer(){
        this.setState((state,props)=>({
            score:state.score+1
        }));
    }
    shouldComponentUpdate(prevState,prevProp){
        // if(this.state.score!==prevState.score)
        //     return false;
        return true;
    }

    componentDidMount(){
        //http req to fetch data
        // console.log("yes");
        var obj={
            id:0,
            question:"What is your name ?",
            options:["a","b","c","d"],
            answer:"a",
            solution:["my name is aman"]
        };
        var ques=[];
        for(var i=0;i<6;i++){
            ques.push(obj);
        }
        // console.log(ques);
        this.setState({questions:ques});
    }
    render(){
        console.log(this.state);
        if(this.state.questions.length!==1){
            const renderElement=this.state.questions.map(i=>{
                console.log(i);
                return(
                    <div>
                        <EachQuestion value={i} CorrectAnswer={this.CorrectAnswer}/>
                    </div>
                )
            })
            return(
                <div style={{marginTop:"100px",textAlign:'center'}}>
                    <h1>{renderElement}</h1>
                </div>
            )
        }
        else{
            return(
                <h1>HEllo</h1>
            )
        }
        // // console.log()
        // if(this.state.questions.length===1){
        //     console.log("0");
        //     return(<h3>yes</h3>);
        // }
        // else{
        //     console.log("1");
        //     return(
        //         <h1>hb</h1>
        //     );
        // }
    }
}

export default Question;
